function splitAddress(adds)
{
    const maxCaracteresPorRenglon = 40; // 40 Máximo de caracteres por renglón
    const palabras = adds.split(', '); // Dividir por las comas y espacios

    let renglones = ['','','']; // Inicializar con tres renglones en blanco
    let renglonIndex = 0;

    for (let i = 0; i < palabras.length; i++)
    {
        const posibleRenglon = renglones[renglonIndex] + (renglones[renglonIndex] ? ', ' : '') + palabras[i];


        if(renglonIndex == 2) //si es el renglon 3 acumulo
        {
            renglones[renglonIndex] = posibleRenglon;
        }
        else if (posibleRenglon.length <= maxCaracteresPorRenglon) //si es cualquier otro renglon veo si cabe
        {
            renglones[renglonIndex] = posibleRenglon;
        }
        else //si es un renglon diferente al 3 y ya no cabe, entonces cambio de renglon
        {
            renglonIndex++;
            /*if (renglonIndex >= 3) {
                break; // Detener después de crear 3 renglones
            }*/
            renglones[renglonIndex] = palabras[i];
        }
    }
    return renglones;
}

function getTextHeight(text, maxWidth, doc, fontSize)
{
    var lineHeight = 1.2; // Altura de línea estimada
    var textWidth = doc.getTextWidth(text);

    // Calcular el número de líneas necesarias
    var numLines = Math.ceil(textWidth / maxWidth);

    // Calcular la altura del texto
    var textHeight = numLines * fontSize * lineHeight;

    return textHeight;
}